frappe.ui.form.on("Service Charge Slab", {
    refresh(frm) {
        // Perform an initial duplicate check
        check_duplicate_items(frm);
    },

    validate: async function(frm) {
        // 1Ô∏è‚É£ Check for duplicate items before saving
        const duplicates = find_duplicate_items(frm);
        if (duplicates.length > 0) {
            frappe.throw(
                __("‚ö†Ô∏è Duplicate Item(s) found: {0}. Please remove them before saving.", 
                    [duplicates.join(", ")])
            );
        }

        // 2Ô∏è‚É£ Check for duplicate Slab Name (fixed filters)
        if (frm.doc.slab_name) {
            const existing = await frappe.db.get_list("Service Charge Slab", {
                filters: [
                    ["slab_name", "=", frm.doc.slab_name],
                    ["name", "!=", frm.doc.name]
                ],
                limit: 1
            });

            if (existing.length > 0) {
                frappe.throw(
                    __("‚ö†Ô∏è Slab Name '{0}' already exists. Please choose another name.", 
                        [frm.doc.slab_name])
                );
            }
        }
    }
});

frappe.ui.form.on("Service Charge Slab Item", {
    item(frm, cdt, cdn) {
        check_duplicate_items(frm);
    },
    table_fxxp_add(frm, cdt, cdn) {
        check_duplicate_items(frm);
    },
});

function find_duplicate_items(frm) {
    const seen = new Set();
    const duplicates = new Set();

    (frm.doc.table_fxxp || []).forEach(r => {
        if (r.item) {
            if (seen.has(r.item)) duplicates.add(r.item);
            seen.add(r.item);
        }
    });
    return Array.from(duplicates);
}

function check_duplicate_items(frm) {
    if (!frm.fields_dict.table_fxxp) return;

    const duplicates = find_duplicate_items(frm);
    const grid = frm.fields_dict.table_fxxp.grid;

    grid.grid_rows.forEach(gr => {
        const row = gr.doc;
        if (duplicates.includes(row.item)) {
            $(gr.wrapper).addClass('duplicate-row');
        } else {
            $(gr.wrapper).removeClass('duplicate-row');
        }
    });

    let warning_area = $(frm.fields_dict.table_fxxp.wrapper).find(".duplicate-warning");
    if (warning_area.length === 0) {
        warning_area = $('<div class="duplicate-warning" style="margin-top:6px;font-size:12px;color:#b30000;"></div>')
            .appendTo(frm.fields_dict.table_fxxp.wrapper);
    }

    if (duplicates.length > 0) {
        warning_area.html(`‚ö†Ô∏è Duplicate item(s) detected: <b>${duplicates.join(", ")}</b>`);
    } else {
        warning_area.html("");
    }
}

frappe.ui.form.on("Service Charge Slab", "onload", function(frm) {
    frappe.dom.add_stylesheet(`
        .grid-body .rows .duplicate-row {
            background-color: #ffe6e6 !important;
        }
    `);
});

frappe.ui.form.on("Customer Service Charge Link", {
    // ‚úÖ New live helper text
    customer(frm) {
        if (!frm.doc.customer) return;

        frappe.db.get_value(
            "Customer Service Charge Link",
            { customer: frm.doc.customer, enabled: 1 },
            ["service_charge_slab"]
        ).then(r => {
            if (r && r.message && r.message.service_charge_slab) {
                frm.set_df_property(
                    "customer",
                    "description",
                    __("‚úÖ Existing Slab: {0}", [r.message.service_charge_slab])
                );
            } else {
                frm.set_df_property(
                    "customer",
                    "description",
                    __("‚ö†Ô∏è No Service Charge Slab linked yet.")
                );
            }
            frm.refresh_field("customer");
        });
    },

    refresh(frm) {
        frm.set_df_property("customer", "description", "");
    },

    // ‚úÖ Keep your duplicate check section here
    validate(frm) {
        if (frm.doc.customer && frm.doc.service_charge_slab) {
            frappe.call({
                method: "frappe.client.get_list",
                args: {
                    doctype: "Customer Service Charge Link",
                    filters: {
                        customer: frm.doc.customer,
                        service_charge_slab: frm.doc.service_charge_slab,
                        name: ["!=", frm.doc.name]
                    },
                    fields: ["name"]
                },
                async: false,
                callback: function (r) {
                    if (r.message && r.message.length > 0) {
                        frappe.throw(
                            __("‚ö†Ô∏è Duplicate combination found: This Service Charge Slab is already linked for the selected Customer.")
                        );
                    }
                }
            });
        }
    }
});

