// 1Ô∏è‚É£ When Customer is selected ‚Üí Load Price List
frappe.ui.form.on('Service Request', {
    customer: async function(frm) {
        if (!frm.doc.customer) return;

        // Fetch default price list for this customer
        let price_list = await frappe.db.get_value(
            "Customer",
            frm.doc.customer,
            "default_price_list"
        );

        if (price_list && price_list.message && price_list.message.default_price_list) {
            frm.set_value("price_list", price_list.message.default_price_list);
        } else {
            frm.set_value("price_list", "");
        }
        frm.refresh_field("price_list");
    }
});

frappe.ui.form.on('Service Request', {
    price_list: function(frm) {
        frm.doc.work_details.forEach(row => {
            frappe.model.set_value(row.doctype, row.name, 'item_code', row.item_code);
        });
    }
});

