frappe.ui.form.on('Service Request Item', {
    item_code: async function(frm, cdt, cdn) {
        let row = frappe.get_doc(cdt, cdn);
        if (!row.item_code || !frm.doc.price_list) return;

        // Fetch rate from Item Price based on Price List
        let price_data = await frappe.db.get_value("Item Price", {
            item_code: row.item_code,
            price_list: frm.doc.price_list
        }, ["price_list_rate"]);

        if (price_data && price_data.message && price_data.message.price_list_rate) {
            frappe.model.set_value(cdt, cdn, "gov_charge", price_data.message.price_list_rate);
        } else {
            frappe.model.set_value(cdt, cdn, "gov_charge", 0);
        }
    }
});

frappe.ui.form.on('Customer Employee Registration', {
    uid_no: function(frm) {
        check_duplicate_inline(frm, 'uid_no');
    },
    passport_no: function(frm) {
        check_duplicate_inline(frm, 'passport_no');
    },
    eid_no: function(frm) {
        check_duplicate_inline(frm, 'eid_no');
    }
});

async function check_duplicate_inline(frm, fieldname) {
    const value = frm.doc[fieldname];
    if (!value) {
        clear_inline_error(frm, fieldname);
        return;
    }

    try {
        const existing = await frappe.db.get_list('Customer Employee Registration', {
            filters: [[fieldname, '=', value], ['name', '!=', frm.doc.name]],
            limit: 1
        });

        const field = frm.fields_dict[fieldname];
        const label = frappe.meta.get_label(frm.doc.doctype, fieldname);

        if (existing.length > 0) {
            // show inline red warning under the field
            field.$wrapper.css("position", "relative");
            if (!field.$wrapper.find('.dup-warning').length) {
                field.$wrapper.append(
                    `<div class="dup-warning" style="color:red; font-size:12px; margin-top:2px;">
                        ${label} already exists!
                    </div>`
                );
            }
        } else {
            clear_inline_error(frm, fieldname);
        }
    } catch (e) {
        console.error(e);
    }
}

function clear_inline_error(frm, fieldname) {
    frm.fields_dict[fieldname].$wrapper.find('.dup-warning').remove();
}

frappe.ui.form.on("Service Request", {
    // Auto Set Tax Category + Template
    customer(frm) {
        if (!frm.doc.customer || !frm.doc.company) return;

        frappe.db.get_value("Customer", frm.doc.customer, "tax_category")
            .then(r => {
                let cat = r.message?.tax_category || "";
                frm.set_value("tax_category", cat);

                if (!cat) return;

                frappe.call({
                    method: "service_workorder.api.get_sales_tax_template",
                    args: { company: frm.doc.company, tax_category: cat },
                    callback(res) {
                        frm.set_value("sales_taxes_and_charges_template", res.message || "");
                        frm.trigger("sales_taxes_and_charges_template");
                    }
                });
            });
    },

    // Load Tax Rows
    sales_taxes_and_charges_template(frm) {
        if (!frm.doc.sales_taxes_and_charges_template) return;

        frappe.call({
            method: "service_workorder.api.load_sales_taxes",
            args: { template_name: frm.doc.sales_taxes_and_charges_template },
            callback(r) {
                frm.clear_table("taxes");
                (r.message || []).forEach(tax => {
                    let row = frm.add_child("taxes");
                    row.charge_type = tax.charge_type;
                    row.account_head = tax.account_head;
                    row.rate = tax.rate;
                    row.description = tax.description;
                });
                frm.refresh_field("taxes");
            }
        });
    }
});

// ============================================================
// SERVICE REQUEST ITEM CHILD SCRIPT
// ============================================================

frappe.ui.form.on("Service Request Item", {
    item_code(frm, cdt, cdn) {
        load_price_list_rate(frm, cdt, cdn);
        load_service_charge_from_slab(frm, cdt, cdn);
    },
    qty(frm, cdt, cdn) {
        calculate_row_total(frm, cdt, cdn);
    },
    gov_charge(frm, cdt, cdn) {
        calculate_row_total(frm, cdt, cdn);
    },
    service_charge(frm, cdt, cdn) {
        calculate_row_total(frm, cdt, cdn);
    }
});

// ============================================================
// LOAD PRICE LIST RATE
// ============================================================

async function load_price_list_rate(frm, cdt, cdn) {
    let row = frappe.get_doc(cdt, cdn);
    if (!row.item_code || !frm.doc.price_list) return;

    let r = await frappe.call({
        method: "frappe.client.get_value",
        args: {
            doctype: "Item Price",
            filters: { item_code: row.item_code, price_list: frm.doc.price_list },
            fieldname: "price_list_rate"
        }
    });

    frappe.model.set_value(cdt, cdn, "gov_charge", r.message?.price_list_rate || 0);
    calculate_row_total(frm, cdt, cdn);
}

// ============================================================
// LOAD SERVICE CHARGE FROM SLAB
// ============================================================

async function load_service_charge_from_slab(frm, cdt, cdn) {
    let row = frappe.get_doc(cdt, cdn);
    if (!frm.doc.service_charge_slab) return;

    let r = await frappe.call({
        method: "service_workorder.api.get_service_charge_from_slab",
        args: { slab_name: frm.doc.service_charge_slab, item_code: row.item_code }
    });

    frappe.model.set_value(cdt, cdn, "service_charge", r.message?.service_charge || 0);
    calculate_row_total(frm, cdt, cdn);
}

// ============================================================
// CALCULATIONS
// ============================================================

function calculate_row_total(frm, cdt, cdn) {
    let row = frappe.get_doc(cdt, cdn);
    let amount = (flt(row.gov_charge) + flt(row.service_charge)) * flt(row.qty);
    frappe.model.set_value(cdt, cdn, "amount", amount);
    calculate_tax_total(frm);
}

function calculate_tax_total(frm) {
    let total_service = 0;
    let base_total = 0;

    (frm.doc.work_details || []).forEach(r => {
        total_service += flt(r.service_charge) * flt(r.qty);
        base_total += (flt(r.gov_charge) + flt(r.service_charge)) * flt(r.qty);
    });

    if (!frm.doc.taxes?.length) return;

    // Row 1 ‚Äî Service Charge Total
    let row1 = frm.doc.taxes[0];
    row1.amount = total_service;
    row1.total = base_total;

    // Row 2 ‚Äî VAT
    if (frm.doc.taxes.length > 1) {
        let vat_row = frm.doc.taxes[1];
        let vat = (total_service * flt(vat_row.rate)) / 100;
        vat_row.amount = vat;
        vat_row.total = base_total + vat;
    }

    frm.refresh_field("taxes");
}

// =======================================================
// SERVICE REQUEST ‚Äì PRICE LIST + GOV CHARGE + SLAB + SERVICE CHARGE
// =======================================================

// -----------------------------
// 1Ô∏è‚É£ LOAD PRICE LIST
// -----------------------------
async function load_price_list(frm) {
    if (!frm.doc.customer || !frm.doc.company) return;

    // Get Customer Default Price List
    const cust = await frappe.db.get_value(
        "Customer",
        frm.doc.customer,
        "default_price_list"
    );

    let price_list = cust.message.default_price_list || "";

    // If Customer has no price list ‚Üí use Company default
    if (!price_list) {
        const comp = await frappe.db.get_value(
            "Company",
            frm.doc.company,
            "default_price_list"
        );
        price_list = comp.message.default_price_list || "";
    }

    frm.set_value("price_list", price_list);
}



// -----------------------------
// 2Ô∏è‚É£ LOAD GOV CHARGE (ITEM PRICE LIST RATE)
// -----------------------------
async function load_gov_charge(frm, cdt, cdn) {
    let row = frappe.get_doc(cdt, cdn);

    if (!row.item_code || !frm.doc.price_list) return;

    const result = await frappe.call({
        method: "frappe.client.get_list",
        args: {
            doctype: "Item Price",
            filters: {
                item_code: row.item_code,
                price_list: frm.doc.price_list
            },
            fields: ["price_list_rate"],
            limit_page_length: 1
        }
    });

    let rate = result.message?.[0]?.price_list_rate || 0;
    frappe.model.set_value(cdt, cdn, "gov_charge", rate);
}



// -----------------------------
// 3Ô∏è‚É£ LOAD SLAB FROM CUSTOMER SERVICE CHARGE LINK (FIXED)
// -----------------------------
async function load_slab(frm) {
    if (!frm.doc.customer) return;

    const res = await frappe.db.get_value(
        "Customer Service Charge Link",
        { customer: frm.doc.customer, enabled: 1 },
        ["service_charge_slab"]   // ONLY THIS FIELD EXISTS
    );

    frm.set_value("service_charge_slab", res.message.service_charge_slab || "");
}



// -----------------------------
// 4Ô∏è‚É£ LOAD SERVICE CHARGE PER ITEM (FROM SLAB)
// -----------------------------
async function load_service_charge(frm, cdt, cdn) {
    let row = frappe.get_doc(cdt, cdn);
    let slab = frm.doc.service_charge_slab;

    if (!slab || !row.item_code) {
        frappe.model.set_value(cdt, cdn, "service_charge", 0);
        return;
    }

    const result = await frappe.call({
        method: "service_workorder.api.get_service_charge_from_slab",
        args: {
            slab_name: slab,
            item_code: row.item_code
        }
    });

    let sc = result.message?.service_charge || 0;
    frappe.model.set_value(cdt, cdn, "service_charge", sc);
}



// =======================================================
// üîÑ HOOK EVENTS
// =======================================================

frappe.ui.form.on("Service Request", {
    // When customer selected ‚Üí load price list & slab
    async customer(frm) {
        await load_price_list(frm);
        await load_slab(frm);
    }
});

frappe.ui.form.on("Service Request Item", {
    async item_code(frm, cdt, cdn) {
        await load_price_list(frm);             // reload price list
        await load_gov_charge(frm, cdt, cdn);   // load gov charge
        await load_service_charge(frm, cdt, cdn); // load slab service charge
    }
});


// =======================================================
// 5Ô∏è‚É£ CALCULATE TOTAL GOV CHARGE + SERVICE CHARGE
// =======================================================

function update_tax_totals(frm) {
    let total_gov = 0;
    let total_service = 0;

    // Calculate totals from Work Details table
    (frm.doc.work_details || []).forEach(row => {
        let qty = flt(row.qty);
        let gov = flt(row.gov_charge);
        let sc = flt(row.service_charge);

        total_gov += gov * qty;
        total_service += sc * qty;
    });

    if (!frm.doc.taxes || frm.doc.taxes.length === 0) return;

    let tax = frm.doc.taxes[0];

    // FIX: Amount = service charge total
    tax.tax_amount = total_service;

    // FIX: Total = gov total + service total
    tax.total = total_gov + total_service;

    frm.refresh_field("taxes");
}



// =======================================================
// 6Ô∏è‚É£ TRIGGER UPDATE WHEN VALUES CHANGE
// =======================================================
frappe.ui.form.on("Service Request Item", {
    qty(frm) { update_tax_totals(frm); },
    gov_charge(frm) { update_tax_totals(frm); },
    service_charge(frm) { update_tax_totals(frm); },
    work_details_remove(frm) { update_tax_totals(frm); }
});

frappe.ui.form.on("Service Request", {
    refresh(frm) { update_tax_totals(frm); }
});

frappe.ui.form.on("Service Request", {
    refresh(frm) {
        // Remove default ERPNext create buttons
        frm.page.remove_inner_button("Sales Order", "Create");
        frm.page.remove_inner_button("Sales Invoice", "Create");
        frm.page.remove_inner_button("Create Sales Order", "Create");
        frm.page.remove_inner_button("Create Sales Invoice", "Create");

        frm.clear_custom_buttons();

        // ======================================================
        // CREATE SALES ORDER BUTTON
        // ======================================================
        frm.add_custom_button("Create Sales Order", () => {
            if (frm.doc.sales_order_ref || frm.doc.sales_invoice_ref) {
                frappe.msgprint("This Service Request already has a linked document. Only one is allowed.");
                return;
            }

            frappe.confirm("Do you want to create a Sales Order?", () => {
                frappe.call({
                    method: "service_workorder.api.create_sales_order_from_service_request",
                    args: { service_request: frm.doc.name },
                    freeze: true,
                    freeze_message: "Creating Sales Order...",
                    callback(r) {
                        if (!r.message) {
                            frappe.msgprint("Failed to create Sales Order");
                            return;
                        }
                        let so_name = r.message.name;
                        frappe.msgprint("Sales Order Created: " + so_name);
                        frappe.set_route("Form", "Sales Order", so_name);
                    }
                });
            });
        }, "Create");

        // ======================================================
        // CREATE SALES INVOICE BUTTON
        // ======================================================
        frm.add_custom_button("Create Sales Invoice", () => {
            if (frm.doc.sales_order_ref || frm.doc.sales_invoice_ref) {
                frappe.msgprint("This Service Request already has a linked document. Only one is allowed.");
                return;
            }

            if (frm.doc.docstatus !== 1) {
                frappe.msgprint("You can only create a Sales Invoice after the Service Request is submitted.");
                return;
            }

            frappe.confirm("Do you want to create a Sales Invoice?", () => {
                frappe.call({
                    method: "service_workorder.api.create_sales_invoice_from_service_request",
                    args: { service_request: frm.doc.name },
                    freeze: true,
                    freeze_message: "Creating Sales Invoice...",
                    callback(r) {
                        if (!r.message) {
                            frappe.msgprint("Failed to create Sales Invoice");
                            return;
                        }
                        let si_name = r.message.name;
                        frappe.msgprint("Sales Invoice Created: " + si_name);
                        frappe.set_route("Form", "Sales Invoice", si_name);
                    }
                });
            });
        }, "Create");
    }
});

frappe.ui.form.on("Service Request", {
    before_cancel(frm) {
        return frappe.call({
            method: "service_workorder.api.validate_sr_cancel_or_delete",
            args: { sr_name: frm.doc.name },
        });
    }
});

frappe.ui.form.on('Customer Employee Registration', {
    first_name: function(frm) {
        frm.set_value('full_name', (frm.doc.first_name || '') + ' ' + (frm.doc.last_name || ''));
    },
    last_name: function(frm) {
        frm.set_value('full_name', (frm.doc.first_name || '') + ' ' + (frm.doc.last_name || ''));
    },
    employee_type: function(frm) {
        if (frm.doc.employee_type === 'Dependent') {
            frm.set_df_property('link_employee', 'hidden', 0);
        } else {
            frm.set_df_property('link_employee', 'hidden', 1);
        }
    },
    customer: function(frm) {
        frm.set_query('link_employee', function() {
            return {
                filters: {
                    customer: frm.doc.customer,
                    employee_type: 'Employee'
                }
            };
        });
    }
});

frappe.ui.form.on('Service Request', {
    refresh(frm) {
        // Filter employee list by selected customer
        frm.set_query('dep_emp_name', () => {
            if (!frm.doc.customer) {
                frappe.msgprint(__('Please select a Customer first'));
                return { filters: { name: ['is', 'set'] } };
            }
            return { filters: { customer_name: frm.doc.customer } };
        });
    },

    async dep_emp_name(frm) {
        // Clear old data
        frm.set_value({
            employee_type: '',
            department_no: '',
            eid_no: '',
            main_employee_name: ''
        });

        if (!frm.doc.dep_emp_name) return;

        const { message: emp } = await frappe.db.get_value(
            'Customer Employee Registration',
            frm.doc.dep_emp_name,
            ['employee_type', 'dep_emp_name', 'dep_no1', 'dep_no2', 'eid_no']
        );
        if (!emp) return;

        const eid = emp.eid_no || '';
        const type = emp.employee_type || '';
        const dept_self = [emp.dep_no1, emp.dep_no2].filter(Boolean).join('/') || '';

        if (type === 'Dependent' && emp.dep_emp_name) {
            // Fetch main employee‚Äôs department info
            const { message: main } = await frappe.db.get_value(
                'Customer Employee Registration',
                emp.dep_emp_name,
                ['dep_no1', 'dep_no2', 'dep_emp_name']
            );

            const main_dept = [main?.dep_no1, main?.dep_no2].filter(Boolean).join('/') || '';

            frm.set_value({
                employee_type: type,
                department_no: main_dept,
                eid_no: eid,
                main_employee_name: main?.dep_emp_name || ''
            });
        } else {
            frm.set_value({
                employee_type: type,
                department_no: dept_self,
                eid_no: eid
            });
        }
        frm.refresh_fields();
    },

    // üîÑ When Customer changes or cleared
    customer(frm) {
        // Reset employee + pricing details
        frm.set_value({
            dep_emp_name: '',
            employee_type: '',
            department_no: '',
            eid_no: '',
