frappe.ui.form.on('Service Request Item', {
    async item_code(frm, cdt, cdn) {
        const row = locals[cdt][cdn];
        const item_code = row.item_code;
        if (!item_code) return;

        // Get price list from parent
        const price_list = frm.doc.price_list || 'Standard Selling';

        // Fetch Gov Charge
        let gov_rate = await get_item_rate(item_code, price_list);
        if (!gov_rate && price_list !== 'Standard Selling') {
            gov_rate = await get_item_rate(item_code, 'Standard Selling');
        }

        frappe.model.set_value(cdt, cdn, 'gov_charge', gov_rate || 0);

        // Optional: calculate amount (if amount field exists)
        if ('amount' in row) {
            frappe.model.set_value(cdt, cdn, 'amount', (row.qty || 1) * (gov_rate || 0));
        }

        // Fetch SRV Item Price
        const srv_item_code = 'SRV ' + item_code;
        let srv_rate = await get_item_rate(srv_item_code, price_list);
        if (!srv_rate && price_list !== 'Standard Selling') {
            srv_rate = await get_item_rate(srv_item_code, 'Standard Selling');
        }

        if (srv_rate) {
            let srv_row = (frm.doc.table_sgku || []).find(r => r.item_code === srv_item_code);
            if (!srv_row) {
                srv_row = frm.add_child('table_sgku', {
                    item_code: srv_item_code,
                    qty: row.qty || 1,
                    service_charge: srv_rate
                });
            } else {
                frappe.model.set_value(srv_row.doctype, srv_row.name, 'service_charge', srv_rate);
                frappe.model.set_value(srv_row.doctype, srv_row.name, 'qty', row.qty || 1);
            }
        }

        frm.refresh_field('work_details');
        frm.refresh_field('table_sgku');
    }
});


