async function get_item_rate(item_code, price_list) {
    const { message } = await frappe.db.get_value(
        'Item Price',
        { item_code: item_code, price_list: price_list, selling: 1 },
        ['price_list_rate']
    );
    return message ? message.price_list_rate : 0;
}

