function update_status_summary(frm) {
    const table = frm.doc.work_details || [];

    if (!table.length) {
        render_html(frm, `<span style="color:gray;">No items yet</span>`);
        frm.page.set_indicator("No Items", "gray");
        return;
    }

    // Count all statuses
    const counts = {};
    table.forEach(row => {
        const status = (row.status || "").trim();
        if (!status) return;
        counts[status] = (counts[status] || 0) + 1;
    });

    // Color map
    const color_map = {
        "Pending": "red",
        "Waiting for Payment": "orange",
        "Completed": "green"
    };

    // Build HTML with counts
    const html_parts = Object.entries(counts).map(([status, count]) => {
        const color = color_map[status] || "gray";
        return `<span style="color:${color}; font-weight:500;">‚óè ${status} (${count})</span>`;
    });

    const html = `<div style="font-size:13px; line-height:1.6;">${html_parts.join(" &nbsp;|&nbsp; ")}</div>`;
    render_html(frm, html);

    // Header indicator
    const all_completed = Object.keys(counts).length === 1 && counts["Completed"] === table.length;
    if (all_completed) {
        frm.page.set_indicator("All Completed", "green");
    } else if (counts["Waiting for Payment"]) {
        frm.page.set_indicator("Waiting for Payment", "orange");
    } else if (counts["Pending"]) {
        frm.page.set_indicator("Pending", "red");
    } else {
        frm.page.set_indicator("In Progress", "gray");
    }
}

// ‚úÖ Correct rendering for ERPNext 15 HTML field
function render_html(frm, html) {
    const field = frm.fields_dict["status_summary"];
    if (!field) {
        console.warn("‚ö†Ô∏è status_summary field not found");
        return;
    }
    if (!field.$wrapper) {
        frappe.ui.form.on("Service Request", {
            refresh(frm) {
                frm.fields_dict["status_summary"].$wrapper.html(html);
            }
        });
        return;
    }
    field.$wrapper.html(html);
}

