// 1Ô∏è‚É£ Rebuild "Taxes and Charges" (table_sgku)
function update_taxes_from_items(frm) {
  frm.clear_table("table_sgku");

  let total_service_charge = 0;
  (frm.doc.work_details || []).forEach(row => {
    total_service_charge += (flt(row.service_charge) || 0) * (flt(row.qty) || 0);
  });

  if (total_service_charge > 0) {
    const t = frm.add_child("table_sgku");
    t.account_head = "Service Charges Income";  // üîÅ adjust if needed
    t.charge_type = "Actual";
    t.tax_amount = total_service_charge;

    // ‚úÖ Fix for "Missing Description" validation
    t.description = "Total Service Charges";  // üëà Default text for mandatory field
  }

  frm.refresh_field("table_sgku");
  console.log("‚úÖ Taxes updated ‚Üí", total_service_charge);
}

// 2Ô∏è‚É£ Fetch slab linked to customer
async function fetch_customer_slab(customer, company) {
  if (!customer || !company) return null;

  const res = await frappe.db.get_list("Customer Service Charge Link", {
    filters: { customer, company, enabled: 1 },
    fields: ["service_charge_slab"],
    limit: 1
  });
  return (res && res.length) ? res[0].service_charge_slab : null;
}

// 3Ô∏è‚É£ Fetch item-specific service charge from slab
async function fetch_item_service_charge(slab_name, item_code) {
  if (!slab_name || !item_code) return null;

  const rows = await frappe.db.get_list("Service Charge Slab Item", {
    filters: { parent: slab_name, item: item_code },
    fields: ["service_charge", "tax_applicable"],
    limit: 1
  });
  return (rows && rows.length) ? rows[0] : null;
}

// 4Ô∏è‚É£ Row amount recalculation (with frappe.model.set_value)
function recalc_row_amount(frm, cdt, cdn) {
  const row = locals[cdt][cdn];
  const qty = flt(row.qty) || 0;
  const gov = flt(row.gov_charge) || 0;
  const srv = flt(row.service_charge) || 0;
  const amount = qty * (gov + srv);

  frappe.model.set_value(cdt, cdn, "amount", amount);
}

