frappe.ui.form.on("Service Request", {
  async customer(frm) {
    if (!frm.doc.customer || !frm.doc.company) return;

    const slab = await fetch_customer_slab(frm.doc.customer, frm.doc.company);
    if (!slab) {
      frappe.msgprint("‚ö†Ô∏è No active Service Charge Slab found for this Customer + Company.");
      frm.set_value("service_charge_slab", "");
      return;
    }

    await frm.set_value("service_charge_slab", slab);
    frappe.show_alert({ message: `Loaded Service Charge Slab: ${slab}`, indicator: "green" });
    console.log("Loaded slab:", slab);

    // Apply slab to existing rows
    for (const row of (frm.doc.work_details || [])) {
      if (!row.item_code) continue;
      const r = await fetch_item_service_charge(slab, row.item_code);
      frappe.model.set_value(row.doctype, row.name, "service_charge", r ? r.service_charge : 0);
      recalc_row_amount(frm, row.doctype, row.name);
    }

    frm.refresh_field("work_details");
    update_taxes_from_items(frm);
  },

  refresh(frm) {
    update_taxes_from_items(frm);
  },

  validate(frm) {
    update_taxes_from_items(frm);
  },

  work_details_add(frm) {
    update_taxes_from_items(frm);
  },

  work_details_remove(frm) {
    update_taxes_from_items(frm);
  }
});

// ======================================================
// Child Table Events ‚Äì Service Request Item
// ======================================================

frappe.ui.form.on("Service Request Item", {
  item_code(frm, cdt, cdn) {
    const row = locals[cdt][cdn];

    if (!frm.doc.service_charge_slab) {
      frappe.msgprint("‚ö†Ô∏è Select a customer with a valid Service Charge Slab first.");
      return;
    }

    if (!row.item_code) return;

    frappe.call({
      method: "service_workorder.api.get_item_service_charge",
      args: { slab: frm.doc.service_charge_slab, item: row.item_code },
      callback: function (r) {
        const srv = (r.message && r.message.service_charge) || 0;
        frappe.model.set_value(cdt, cdn, "service_charge", srv);
        recalc_row_amount(frm, cdt, cdn);
        update_taxes_from_items(frm);
      }
    });
  },

  qty(frm, cdt, cdn) {
    recalc_row_amount(frm, cdt, cdn);
    update_taxes_from_items(frm);
  },

  gov_charge(frm, cdt, cdn) {
    recalc_row_amount(frm, cdt, cdn);
    update_taxes_from_items(frm);
  },

  service_charge(frm, cdt, cdn) {
    recalc_row_amount(frm, cdt, cdn);
    update_taxes_from_items(frm);
  }
});
